    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        String name = jname.getText();
        String uname = juname.getText();
        String phone = jphone.getText();
        String email = jemail.getText();
        String pass = jpass.getText();

        try{
            Statement s = (Statement) db.mycon().createStatement();
            s.executeUpdate("INSERT INTO users (Name, User_Name, Email, Password, phone) " +
                "VALUES ('" + name + "', '" + uname + "', '" + email + "', '" + pass + "', '" + phone + "')");

            JOptionPane.showMessageDialog(rootPane, "YOU HAVE SUCCEFULLY CREATED AN ACCOUNT. GO BACK TO LOGIN");
            this.setVisible(false);
            new loginF().setVisible(true);
        }catch (HeadlessException | SQLException e){

        }
    }//GEN-LAST:event_jButton1ActionPerformed

