    private void jckboxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jckboxActionPerformed
        if(jckbox.isSelected()){
            jpass.setEchoChar((char)0);
        }
        else{
            jpass.setEchoChar('*');
        }
    }//GEN-LAST:event_jckboxActionPerformed

